mapper.select:
select
  file_id, file_oldFileName, file_newFileName, file_ext, file_path, file_size, file_type, file_isImg, file_downcounts, file_uploadTime
from file_updown.fileupdown_file


        and file_id = #{fileId}


        and file_oldFileName = #{fileOldfilename}


        and file_newFileName = #{fileNewfilename}


        and file_ext = #{fileExt}


        and file_path = #{filePath}


        and file_size = #{fileSize}


        and file_type = #{fileType}


        and file_isImg = #{fileIsimg}


        and file_downcounts = #{fileDowncounts}


        and file_uploadTime = #{fileUploadtime}
